function replaceFirstItem(){const firstItem=document.querySelector("li:first-child"),li=document.createElement("li");li.textContent="Replaced First",firstItem.replaceWith(li)}const replaceSecondItem=()=>{const secondItem=document.querySelector("li:nth-child(2)");secondItem.outerHTML="<li>Replaced Second</li>"},replaceAllItems=()=>{const lis=document.querySelectorAll("li");lis.forEach((item,index)=>{item.innerHTML=1===index?"Replaced Second":"Replace All"}),lis.forEach((item,index)=>item.outerHTML=2===index?"<li>Replaced Third</li>":"<li>Replace All</li>")};function replaceChildHeading(){const header=document.querySelector("header"),h1=document.querySelector("header h1"),h2=document.createElement("h2");h2.id="app-title",h2.textContent="Shopping List h2",header.replaceChild(h2,h1)}replaceFirstItem(),replaceSecondItem(),replaceAllItems(),replaceChildHeading();