let intervalID;function startChange(){intervalID||(intervalID=setInterval(changeRandomColor,1e3))}function changeRandomColor(){const randomColor=Math.floor(16777215*Math.random()).toString(16);document.body.style.backgroundColor=`#${randomColor}`}function stopChange(){clearInterval(intervalID)}document.getElementById("start").addEventListener("click",startChange),document.getElementById("stop").addEventListener("click",stopChange);