const posts=[{title:"Post One",body:"This is post one"},{title:"Post Two",body:"This is post two"}];function createPost(post,cb){setTimeout(()=>{posts.push(post),cb()},2e3)}function getPosts(){setTimeout(()=>{posts.forEach((function(post){const div=document.createElement("div");div.innerHTML=`<strong>${post.title}</strong> - ${post.body}`,document.querySelector("#posts").appendChild(div)}))},1e3)}createPost({title:"Post three",body:"this is post three"},getPosts);