const xhr=new XMLHttpRequest;xhr.open("GET","https://api.github.com/users/jayryannacua11/repos"),xhr.onreadystatechange=function(){if(4===this.readyState&&200===this.status){const data=JSON.parse(this.responseText);data.forEach(repo=>{const li=document.createElement("li");li.innerHTML=`<strong>${repo.name}</strong> - <a href="${repo.html_url}" target="_blank">Click to view</a>`,document.querySelector("#results").appendChild(li)})}},xhr.send();