function getData(endpoint,cb){const xhr=new XMLHttpRequest;xhr.open("GET",endpoint),xhr.onreadystatechange=function(){4===this.readyState&&200===this.status&&cb(JSON.parse(this.responseText))},setTimeout(()=>{xhr.send()},Math.floor(3e3*Math.random())+1e3)}getData("./movies.json",data=>{console.log("Movies:"),console.log(data),getData("./actors.json",data=>{console.log("Actors:"),console.log(data),getData("./directors.json",data=>{console.log("Directors:"),console.log(data)})})});