function getData(endpoint){return new Promise((resolve,reject)=>{const xhr=new XMLHttpRequest;xhr.open("GET",endpoint),xhr.onreadystatechange=function(){4===this.readyState&&(200===this.status?resolve(JSON.parse(this.responseText)):reject("Something went wrong"))},setTimeout(()=>{xhr.send()},Math.floor(3e3*Math.random())+1e3)})}getData("./movies.json").then(movies=>(console.log(movies),getData("./actors.json"))).then(actors=>(console.log(actors),getData("./directors.json"))).then(directors=>{console.log(directors)}).catch(error=>console.log(error));