function getData(endpoint){return new Promise((resolve,reject)=>{const xhr=new XMLHttpRequest;xhr.open("GET",endpoint),xhr.onreadystatechange=function(){4===this.readyState&&(200===this.status?resolve(JSON.parse(this.responseText)):reject("Something went wrong"))},setTimeout(()=>{xhr.send()},Math.floor(3e3*Math.random())+1e3)})}const moviesPromise=getData("./movies.json"),actorsPromise=getData("./actors.json"),directorsPromise=getData("./directors.json"),dummyPromise=new Promise((resolve,reject)=>{resolve("Hello World")});Promise.all([moviesPromise,actorsPromise,directorsPromise,dummyPromise]).then(data=>{console.log(data)}).catch(err=>console.log(err));