function fetchUser(){showSpinner(),fetch("https://randomuser.me/api1").then(res=>{if(!res.ok)throw new Error("Request Failed");return res.json()}).then(data=>{hideSpinner(),displayUser(data.results[0])}).catch(err=>{hideSpinner(),document.querySelector("#user").innerHTML=`<p class="text-xl text-center text-red-500 mb-5">${err}</p>`})}function displayUser(user){const userDisplay=document.querySelector("#user");"female"===user.gender?document.body.style.backgroundColor="rebeccapurple":document.body.style.backgroundColor="steelblue",userDisplay.innerHTML=`\n  <div class="flex justify-between">\n  <div class="flex">\n    <img\n      class="w-48 h-48 rounded-full mr-8"\n      src="${user.picture.large}"\n    />\n    <div class="space-y-3">\n      <p class="text-xl">\n        <span class="font-bold">Name: </span>${user.name.first} ${user.name.last}\n      </p>\n      <p class="text-xl">\n        <span class="font-bold">Email: </span> ${user.email}\n      </p>\n      <p class="text-xl">\n        <span class="font-bold">Phone: </span> ${user.phone}\n      </p>\n      <p class="text-xl">\n        <span class="font-bold">Location: </span> ${user.location.city} ${user.location.country}\n      </p>\n      <p class="text-xl"><span class="font-bold">Age: </span> ${user.dob.age}</p>\n    </div>\n  </div>\n</div>\n  `}function showSpinner(){document.querySelector(".spinner").style.display="block"}function hideSpinner(){document.querySelector(".spinner").style.display="none"}document.querySelector("#generate").addEventListener("click",fetchUser),fetchUser();