const video=document.getElementById("video"),play=document.getElementById("play"),stop=document.getElementById("stop"),progress=document.getElementById("progress"),timestamp=document.getElementById("timestamp"),playPause=()=>video.paused?video.play():video.pause(),updateIcon=()=>video.paused?play.innerHTML='<i class="fa fa-play fa-2x"></i>':play.innerHTML='<i class="fa fa-pause fa-2x"></i>',stopVideo=()=>{video.currentTime=0,video.pause()},updateProgress=()=>{progress.value=video.currentTime/video.duration*100;let minutes=Math.floor(video.currentTime/60);minutes<10&&(minutes="0"+String(minutes));let seconds=Math.floor(video.currentTime%60);seconds<10&&(seconds="0"+String(seconds)),timestamp.innerHTML=`${minutes}:${seconds}`},setProgress=()=>{video.currentTime=+progress.value*video.duration/100};video.addEventListener("click",playPause),video.addEventListener("play",updateIcon),video.addEventListener("pause",updateIcon),video.addEventListener("timeupdate",updateProgress),play.addEventListener("click",playPause),stop.addEventListener("click",stopVideo),progress.addEventListener("click",setProgress);