const ball=document.getElementById("ball"),play=document.getElementById("play"),pause=document.getElementById("pause"),reverse=document.getElementById("reverse");let speed=1;const speedUp=document.getElementById("speed-up"),slowDown=document.getElementById("slow-down"),rollAnimation=[{transform:"rotate(0) translate3D(-50%, -50%, 0)",color:"white"},{color:"blue",offset:.3},{transform:"rotate(360deg) translate3D(-50%, -50%, 0)",color:"white"}],rollOptions={duration:3e3,iterations:1/0},roll=ball.animate(rollAnimation,rollOptions),showSpeed=speed=>{const speedText=document.querySelector("h1 span");speedText.textContent=speed};play.addEventListener("click",()=>{roll.playbackRate=1,roll.play()}),pause.addEventListener("click",()=>roll.pause()),reverse.addEventListener("click",()=>roll.reverse()),speedUp.addEventListener("click",()=>{roll.playbackRate<8&&(roll.playbackRate*=2,-2===speed&&(speed=0),speed++),showSpeed(speed)}),slowDown.addEventListener("click",()=>{roll.playbackRate>.125&&(roll.playbackRate*=.5,1===speed&&(speed=-1),speed--),showSpeed(speed)});