const SpeechRecognition=window.SpeechRecognition||window.webkitSpeechRecognition,rec=new SpeechRecognition;rec.lang="en-US",rec.continuous=!0,rec.onresult=function(e){const acceptedColors=["red","blue","green","white","black"];for(let i=e.resultIndex;i<e.results.length;i++){const script=e.results[i][0].transcript.toLowerCase().trim().replace(/^\.+|\.+$/g,"");acceptedColors.includes(script)?document.body.style.backgroundColor=script:console.log(script)}},rec.start();